<script type="text/javascript">$(document).ready(function(){var o={rules:[{type:"empty",prompt:"{{ @login.form.validation.empty_password }}"},{type:"minLength[6]",prompt:"{{ @login.form.validation.valid_password }}"}]},r={rules:[{type:"match[new_password]",prompt:"{{ @password.reset.form.passwords.not.equal }}"}]};$(".form").form({on:"blur",fields:{old_password:o,new_password:o,new_password2:r}})});</script>