<script type="text/javascript">$(document).ready(function(){var e=$("#timezone-dropdown");$.each(moment.tz.names(),function(n,t){e.append('<option value="'+t+'" '+("<?php echo $SITE['TIMEZONE']; ?>"==t?"selected":"")+">"+t+"</option>")}),$("#settings-tabs-menu .item").tab(),$(".ui.dropdown").dropdown(),$(".ui.toggle.checkbox").checkbox({onChecked:function(){$(this).val(1).parent().find("input[type=hidden]").prop("disabled",!0),$(this).closest(".state-changing").closest(".fields-group").find(".field").not(".state-insensitive").removeClass("disabled")},onUnchecked:function(){$(this).val(0).parent().find("input[type=hidden]").prop("disabled",!1),$(this).closest(".state-changing").closest(".fields-group").find(".field").not(".state-insensitive").addClass("disabled")}}),$(".question.icon").popup()});</script>